-- Create teacher_definitions table if not exists
CREATE TABLE IF NOT EXISTS teacher_definitions (
    id SERIAL PRIMARY KEY,
    topic_id VARCHAR(10) NOT NULL,
    term VARCHAR(255) NOT NULL,
    definition TEXT NOT NULL,
    theme VARCHAR(100),
    created_at TIMESTAMP DEFAULT NOW()
);

-- Clear existing teacher definitions
DELETE FROM teacher_definitions;

